generated.css:
body{
    font-family:Verdana, Geneva, Tahoma, sans-serif
}
h1,h2,h3,h4,h5,h6{
    font-weight:700;
    font-family:Georgia, 'Times New Roman', Times, serif;
}
hr.star-dark,hr.star-light{
    max-width:15rem;
    padding:0;text-align:center;
    border:none;
    border-top:solid .25rem;
    margin-top:2.5rem;
    margin-bottom:2.5rem;
    margin-left:auto;
    margin-right:auto
}
hr.star-dark:after,hr.star-light:after{
    position:relative;
    top:-.8em;display:inline-block;
    padding:0 .25em;content:'\f005';
    font-family:"Font Awesome 5 Free";
    font-weight:900;
    font-size:2em
}
hr.star-light{
    border-color:#fff
}
hr.star-light:after{
    color:#fff;
    background-color:#18bc9c
}
hr.star-dark{
    border-color:#2c3e50
}
hr.star-dark:after{
    color:#2c3e50;
    background-color:#fff
}
section{
    padding:6rem 0
}
section h2{
    font-size:1.75rem;
